import React, { useState, useRef, useEffect, useCallback } from 'react';
import { Play, Pause, Camera, Target, TrendingUp, Clock, Award, AlertCircle, BarChart3, Brain, Key, Mouse, Monitor } from 'lucide-react';

const ProgressTracker = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [sessionTime, setSessionTime] = useState(0);
  const [screenshots, setScreenshots] = useState([]);
  const [currentGoal, setCurrentGoal] = useState('Complete project documentation');
  const [apiKey, setApiKey] = useState('');
  const [isApiKeySet, setIsApiKeySet] = useState(false);
  const [progressData, setProgressData] = useState({
    todayProgress: 0,
    weeklyProgress: 0,
    goalCompletion: 0,
    efficiency: 0
  });
  const [aiAnalysis, setAiAnalysis] = useState(null);
  const [sessions, setSessions] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisLog, setAnalysisLog] = useState([]);
  const [captureStats, setCaptureStats] = useState({
    clicks: 0,
    keystrokes: 0,
    windowChanges: 0
  });

  // Tracking variables
  const intervalRef = useRef(null);
  const streamRef = useRef(null);
  const keystrokeCountRef = useRef(0);
  const lastWindowTitleRef = useRef('');
  const isAnalyzingRef = useRef(false);

  // Timer effect
  useEffect(() => {
    if (isRecording) {
      intervalRef.current = setInterval(() => {
        setSessionTime(prev => prev + 1);
      }, 1000);
    } else {
      clearInterval(intervalRef.current);
    }
    return () => clearInterval(intervalRef.current);
  }, [isRecording]);

  // Event listeners for capture triggers
  useEffect(() => {
    if (isRecording && streamRef.current) {
      const handleClick = () => {
        setCaptureStats(prev => ({ ...prev, clicks: prev.clicks + 1 }));
        triggerScreenshot('click');
      };

      const handleKeydown = () => {
        keystrokeCountRef.current++;
        setCaptureStats(prev => ({ ...prev, keystrokes: prev.keystrokes + 1 }));

        if (keystrokeCountRef.current >= 20) {
          keystrokeCountRef.current = 0;
          triggerScreenshot('keystrokes');
        }
      };

      const handleWindowChange = () => {
        const currentTitle = document.title;
        if (currentTitle !== lastWindowTitleRef.current) {
          lastWindowTitleRef.current = currentTitle;
          setCaptureStats(prev => ({ ...prev, windowChanges: prev.windowChanges + 1 }));
          triggerScreenshot('window_change');
        }
      };

      // Add global event listeners
      document.addEventListener('click', handleClick);
      document.addEventListener('keydown', handleKeydown);
      document.addEventListener('visibilitychange', handleWindowChange);
      window.addEventListener('focus', handleWindowChange);

      return () => {
        document.removeEventListener('click', handleClick);
        document.removeEventListener('keydown', handleKeydown);
        document.removeEventListener('visibilitychange', handleWindowChange);
        window.removeEventListener('focus', handleWindowChange);
      };
    }
  }, [isRecording]);

  const triggerScreenshot = useCallback(async (trigger) => {
    if (!streamRef.current || isAnalyzingRef.current) return;

    try {
      const canvas = document.createElement('canvas');
      const video = document.createElement('video');
      video.srcObject = streamRef.current;
      video.play();

      video.addEventListener('loadedmetadata', async () => {
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        const ctx = canvas.getContext('2d');
        ctx.drawImage(video, 0, 0);

        canvas.toBlob(async (blob) => {
          const timestamp = new Date().toLocaleTimeString();
          const imageUrl = URL.createObjectURL(blob);

          const newScreenshot = {
            id: Date.now(),
            timestamp,
            url: imageUrl,
            blob: blob,
            trigger: trigger,
            analyzed: false
          };

          setScreenshots(prev => [...prev, newScreenshot]);

          // Analyze with OpenAI immediately
          if (isApiKeySet) {
            await analyzeScreenshotWithAI(newScreenshot);
          }
        }, 'image/jpeg', 0.9);
      });
    } catch (error) {
      console.error('Screenshot capture failed:', error);
    }
  }, [isApiKeySet]);

  const analyzeScreenshotWithAI = async (screenshot) => {
    if (isAnalyzingRef.current) return;

    isAnalyzingRef.current = true;
    setIsAnalyzing(true);

    try {
      // Convert blob to base64
      const base64 = await blobToBase64(screenshot.blob);

      // Call OpenAI Vision API
      const analysis = await callOpenAIVision(base64, currentGoal);

      // Update screenshot with AI analysis
      setScreenshots(prev => prev.map(s => 
        s.id === screenshot.id 
          ? { ...s, analyzed: true, aiAnalysis: analysis }
          : s
      ));

      // Add to analysis log
      setAnalysisLog(prev => [...prev, {
        timestamp: screenshot.timestamp,
        trigger: screenshot.trigger,
        productivity: analysis.productivityScore,
        activity: analysis.activity,
        insights: analysis.insights,
        fullAnalysis: analysis
      }]);

    } catch (error) {
      console.error('AI analysis failed:', error);
      setAnalysisLog(prev => [...prev, {
        timestamp: screenshot.timestamp,
        trigger: screenshot.trigger,
        productivity: 0,
        activity: 'Analysis Failed',
        insights: ['AI analysis unavailable'],
        error: error.message
      }]);
    } finally {
      isAnalyzingRef.current = false;
      setIsAnalyzing(false);
    }
  };

  const callOpenAIVision = async (base64Image, goal) => {
    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: 'gpt-4-vision-preview',
          messages: [
            {
              role: 'user',
              content: [
                {
                  type: 'text',
                  text: `Analyze this work session screenshot. The user's goal is: "${goal}". 

                  Please provide analysis in this exact JSON format:
                  {
                    "productivityScore": <number 0-100>,
                    "activity": "<activity type>",
                    "insights": ["<insight 1>", "<insight 2>"],
                    "focusLevel": <number 0-100>,
                    "distractions": ["<distraction 1>", "<distraction 2>"],
                    "goalAlignment": <number 0-100>,
                    "recommendations": ["<recommendation 1>", "<recommendation 2>"],
                    "applications": ["<app 1>", "<app 2>"],
                    "timeOfDay": "<morning/afternoon/evening>",
                    "workPattern": "<description>"
                  }

                  Score based on:
                  - How focused the work appears
                  - Alignment with stated goal
                  - Evidence of productive activity
                  - Presence of distractions
                  - Application usage patterns`
                },
                {
                  type: 'image_url',
                  image_url: {
                    url: base64Image
                  }
                }
              ]
            }
          ],
          max_tokens: 500
        })
      });

      if (!response.ok) {
        throw new Error(`OpenAI API error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      const content = data.choices[0].message.content;

      // Try to parse JSON from the response
      try {
        const jsonMatch = content.match(/\{[\s\S]*\}/);
        if (jsonMatch) {
          return JSON.parse(jsonMatch[0]);
        }
      } catch (parseError) {
        console.error('JSON parsing failed:', parseError);
      }

      // Fallback parsing if JSON fails
      return parseAIResponse(content);

    } catch (error) {
      console.error('OpenAI API call failed:', error);
      throw error;
    }
  };

  const parseAIResponse = (content) => {
    // Fallback parser for non-JSON responses
    return {
      productivityScore: extractNumber(content, 'productivity') || 50,
      activity: extractActivity(content) || 'General Work',
      insights: extractInsights(content) || ['AI analysis completed'],
      focusLevel: extractNumber(content, 'focus') || 50,
      distractions: extractDistractions(content) || [],
      goalAlignment: extractNumber(content, 'goal') || 50,
      recommendations: extractRecommendations(content) || [],
      applications: extractApplications(content) || [],
      timeOfDay: extractTimeOfDay(),
      workPattern: 'Analysis in progress'
    };
  };

  const extractNumber = (text, keyword) => {
    const regex = new RegExp(`${keyword}[:\\s]*([0-9]{1,3})`, 'i');
    const match = text.match(regex);
    return match ? parseInt(match[1]) : null;
  };

  const extractActivity = (text) => {
    const activities = ['coding', 'writing', 'research', 'communication', 'design', 'planning'];
    const found = activities.find(activity => 
      text.toLowerCase().includes(activity)
    );
    return found ? found.charAt(0).toUpperCase() + found.slice(1) : 'General Work';
  };

  const extractInsights = (text) => {
    const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 10);
    return sentences.slice(0, 3).map(s => s.trim());
  };

  const extractDistractions = (text) => {
    const commonDistractions = ['social media', 'email', 'notifications', 'multiple tabs'];
    return commonDistractions.filter(d => text.toLowerCase().includes(d));
  };

  const extractRecommendations = (text) => {
    const sentences = text.split(/[.!?]+/).filter(s => 
      s.toLowerCase().includes('recommend') || 
      s.toLowerCase().includes('suggest') ||
      s.toLowerCase().includes('should')
    );
    return sentences.slice(0, 2).map(s => s.trim());
  };

  const extractApplications = (text) => {
    const commonApps = ['chrome', 'vscode', 'slack', 'word', 'excel', 'figma', 'notion'];
    return commonApps.filter(app => text.toLowerCase().includes(app));
  };

  const extractTimeOfDay = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'morning';
    if (hour < 17) return 'afternoon';
    return 'evening';
  };

  const blobToBase64 = (blob) => {
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.readAsDataURL(blob);
    });
  };

  const startScreenShare = async () => {
    try {
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: { 
          mediaSource: 'screen',
          width: { ideal: 1920 },
          height: { ideal: 1080 },
          frameRate: { ideal: 2 }
        },
        audio: false
      });

      streamRef.current = stream;

      stream.getVideoTracks()[0].addEventListener('ended', () => {
        setIsRecording(false);
        endSession();
      });

      return true;
    } catch (error) {
      console.error('Screen sharing failed:', error);
      alert('Screen sharing permission required for progress tracking');
      return false;
    }
  };

  const toggleRecording = async () => {
    if (!isApiKeySet) {
      alert('Please set your OpenAI API key first');
      return;
    }

    if (isRecording) {
      setIsRecording(false);
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
      endSession();
    } else {
      const screenShareStarted = await startScreenShare();
      if (screenShareStarted) {
        setIsRecording(true);
        setSessionTime(0);
        setScreenshots([]);
        setAnalysisLog([]);
        setCaptureStats({ clicks: 0, keystrokes: 0, windowChanges: 0 });
        keystrokeCountRef.current = 0;
      }
    }
  };

  const endSession = async () => {
    const session = {
      id: Date.now(),
      duration: sessionTime,
      screenshots: screenshots.length,
      timestamp: new Date().toLocaleString(),
      goal: currentGoal,
      analysisData: analysisLog,
      captureStats: captureStats
    };

    setSessions(prev => [...prev, session]);
    await performFinalAnalysis(session);
  };

  const performFinalAnalysis = async (session) => {
    if (analysisLog.length === 0) return;

    const validAnalyses = analysisLog.filter(log => !log.error);
    if (validAnalyses.length === 0) return;

    const avgProductivity = validAnalyses.reduce((sum, log) => sum + log.productivity, 0) / validAnalyses.length;
    const avgFocus = validAnalyses.reduce((sum, log) => sum + (log.fullAnalysis?.focusLevel || 50), 0) / validAnalyses.length;
    const avgGoalAlignment = validAnalyses.reduce((sum, log) => sum + (log.fullAnalysis?.goalAlignment || 50), 0) / validAnalyses.length;

    const activityBreakdown = validAnalyses.reduce((acc, log) => {
      acc[log.activity] = (acc[log.activity] || 0) + 1;
      return acc;
    }, {});

    const allInsights = validAnalyses.flatMap(log => log.insights || []);
    const allRecommendations = validAnalyses.flatMap(log => log.fullAnalysis?.recommendations || []);

    const finalAnalysis = {
      efficiency: Math.round(avgProductivity),
      focusScore: Math.round(avgFocus),
      goalProgress: Math.round(avgGoalAlignment),
      insights: [...new Set(allInsights)].slice(0, 5),
      recommendations: [...new Set(allRecommendations)].slice(0, 5),
      nextSteps: generateNextSteps(currentGoal, avgProductivity),
      activityBreakdown,
      sessionSummary: `Analyzed ${validAnalyses.length} AI insights with ${Math.round(avgProductivity)}% average productivity`,
      captureBreakdown: captureStats
    };

    setAiAnalysis(finalAnalysis);

    setProgressData(prev => ({
      todayProgress: Math.min(prev.todayProgress + (session.duration / 480) * 100, 100),
      weeklyProgress: Math.min(prev.weeklyProgress + (session.duration / 2400) * 100, 100),
      goalCompletion: finalAnalysis.goalProgress,
      efficiency: finalAnalysis.efficiency
    }));
  };

  const generateNextSteps = (goal, productivity) => {
    const steps = [];

    if (productivity > 80) {
      steps.push('Maintain current high productivity momentum');
      steps.push('Consider documenting successful work patterns');
    } else if (productivity > 60) {
      steps.push('Identify peak productivity periods for important tasks');
      steps.push('Minimize context switching during focused work');
    } else {
      steps.push('Review and eliminate key productivity blockers');
      steps.push('Consider shorter, more focused work sessions');
    }

    if (goal.toLowerCase().includes('documentation')) {
      steps.push('Continue systematic documentation approach');
    } else if (goal.toLowerCase().includes('code')) {
      steps.push('Plan next development milestones');
    }

    return steps;
  };

  const handleApiKeySubmit = () => {
    if (apiKey.trim().startsWith('sk-')) {
      setIsApiKeySet(true);
    } else {
      alert('Please enter a valid OpenAI API key (starts with sk-)');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // API Key Setup Screen
  if (!isApiKeySet) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 flex items-center justify-center">
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-8 border border-white/20 max-w-md w-full mx-4">
          <h2 className="text-2xl font-bold text-white mb-6 text-center">Setup OpenAI API Key</h2>
          <p className="text-white/80 mb-6 text-sm">
            This MVP uses OpenAI's Vision API for real AI analysis. You need an API key to continue.
          </p>
          <div className="space-y-4">
            <input
              type="password"
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
              placeholder="sk-..."
              className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-3 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-purple-400"
            />
            <button
              onClick={handleApiKeySubmit}
              className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white font-bold py-3 px-4 rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all duration-300"
            >
              Start AI Analysis
            </button>
          </div>
          <p className="text-white/60 text-xs mt-4 text-center">
            Get your API key from platform.openai.com/api-keys
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900">
      <div className="container mx-auto px-4 py-8">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">AI Progress Tracker MVP</h1>
          <p className="text-indigo-200">Real OpenAI Vision Analysis • Intelligent Capture Triggers</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">

          {/* Recording Control Panel */}
          <div className="lg:col-span-2">
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-semibold text-white flex items-center gap-2">
                  <Target className="w-6 h-6" />
                  Live AI Session
                </h2>
                <div className="flex items-center gap-4 text-white/80">
                  <Clock className="w-4 h-4" />
                  <span className="text-sm">{formatTime(sessionTime)}</span>
                  {isAnalyzing && (
                    <div className="flex items-center gap-2">
                      <Brain className="w-4 h-4 animate-pulse text-blue-400" />
                      <span className="text-sm text-blue-400">AI Processing...</span>
                    </div>
                  )}
                </div>
              </div>

              <div className="mb-6">
                <label className="block text-white/80 text-sm font-medium mb-2">
                  Session Goal
                </label>
                <input
                  type="text"
                  value={currentGoal}
                  onChange={(e) => setCurrentGoal(e.target.value)}
                  className="w-full bg-white/10 border border-white/30 rounded-lg px-4 py-2 text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-purple-400"
                  placeholder="What are you working on today?"
                />
              </div>

              <div className="flex justify-center mb-6">
                <button
                  onClick={toggleRecording}
                  className={`w-20 h-20 rounded-full flex items-center justify-center text-white text-2xl font-bold transition-all duration-300 ${
                    isRecording 
                      ? 'bg-red-500 hover:bg-red-600 shadow-lg shadow-red-500/50' 
                      : 'bg-green-500 hover:bg-green-600 shadow-lg shadow-green-500/50'
                  }`}
                >
                  {isRecording ? <Pause className="w-8 h-8" /> : <Play className="w-8 h-8 ml-1" />}
                </button>
              </div>

              {/* Capture Triggers */}
              {isRecording && (
                <div className="grid grid-cols-3 gap-4 mb-6">
                  <div className="bg-blue-500/20 rounded-lg p-4 text-center border border-blue-500/30">
                    <Mouse className="w-6 h-6 text-blue-400 mx-auto mb-2" />
                    <div className="text-2xl font-bold text-white">{captureStats.clicks}</div>
                    <div className="text-blue-200 text-sm">Clicks</div>
                  </div>
                  <div className="bg-green-500/20 rounded-lg p-4 text-center border border-green-500/30">
                    <Key className="w-6 h-6 text-green-400 mx-auto mb-2" />
                    <div className="text-2xl font-bold text-white">{Math.floor(captureStats.keystrokes / 20)}</div>
                    <div className="text-green-200 text-sm">Captures</div>
                  </div>
                  <div className="bg-purple-500/20 rounded-lg p-4 text-center border border-purple-500/30">
                    <Monitor className="w-6 h-6 text-purple-400 mx-auto mb-2" />
                    <div className="text-2xl font-bold text-white">{captureStats.windowChanges}</div>
                    <div className="text-purple-200 text-sm">Window Changes</div>
                  </div>
                </div>
              )}

              {/* Live AI Analysis Feed */}
              {analysisLog.length > 0 && (
                <div>
                  <h3 className="text-white font-semibold mb-3 flex items-center gap-2">
                    <Brain className="w-5 h-5" />
                    Real-time AI Analysis
                  </h3>
                  <div className="bg-black/20 rounded-lg p-4 max-h-60 overflow-y-auto space-y-3">
                    {analysisLog.slice(-5).map((log, index) => (
                      <div key={index} className="bg-white/10 rounded-lg p-3">
                        <div className="flex justify-between items-start mb-2">
                          <div className="flex items-center gap-2">
                            <span className="text-sm font-medium text-white">{log.activity}</span>
                            <span className="text-xs bg-blue-500/20 text-blue-300 px-2 py-1 rounded">
                              {log.trigger}
                            </span>
                          </div>
                          <div className={`px-2 py-1 rounded text-xs font-bold ${
                            log.productivity > 80 ? 'bg-green-500/20 text-green-300' :
                            log.productivity > 60 ? 'bg-yellow-500/20 text-yellow-300' :
                            'bg-red-500/20 text-red-300'
                          }`}>
                            {log.productivity}%
                          </div>
                        </div>
                        {log.insights && log.insights.length > 0 && (
                          <div className="text-xs text-white/70">
                            {log.insights[0]}
                          </div>
                        )}
                        <div className="text-xs text-white/50 mt-1">{log.timestamp}</div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* AI Analysis Results */}
          <div className="space-y-6">
            {/* Progress Metrics */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                <BarChart3 className="w-5 h-5" />
                AI Progress Metrics
              </h3>

              <div className="space-y-4">
                <div>
                  <div className="flex justify-between text-sm text-white/80 mb-1">
                    <span>Goal Completion</span>
                    <span>{Math.round(progressData.goalCompletion)}%</span>
                  </div>
                  <div className="w-full bg-white/20 rounded-full h-2">
                    <div 
                      className="bg-gradient-to-r from-green-400 to-blue-500 h-2 rounded-full transition-all duration-500"
                      style={{ width: `${progressData.goalCompletion}%` }}
                    ></div>
                  </div>
                </div>

                <div>
                  <div className="flex justify-between text-sm text-white/80 mb-1">
                    <span>AI Efficiency Score</span>
                    <span>{Math.round(progressData.efficiency)}%</span>
                  </div>
                  <div className="w-full bg-white/20 rounded-full h-2">
                    <div 
                      className="bg-gradient-to-r from-purple-400 to-pink-500 h-2 rounded-full transition-all duration-500"
                      style={{ width: `${progressData.efficiency}%` }}
                    ></div>
                  </div>
                </div>

                <div className="text-center pt-2">
                  <div className="text-2xl font-bold text-white">{screenshots.length}</div>
                  <div className="text-white/60 text-sm">AI Screenshots Analyzed</div>
                </div>
              </div>
            </div>

            {/* Final AI Analysis */}
            {aiAnalysis && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                  <Award className="w-5 h-5" />
                  OpenAI Session Analysis
                </h3>

                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4 text-center">
                    <div>
                      <div className="text-2xl font-bold text-green-400">{aiAnalysis.efficiency}%</div>
                      <div className="text-xs text-white/60">Efficiency</div>
                    </div>
                    <div>
                      <div className="text-2xl font-bold text-blue-400">{aiAnalysis.focusScore}%</div>
                      <div className="text-xs text-white/60">Focus Score</div>
                    </div>
                  </div>

                  {aiAnalysis.sessionSummary && (
                    <div className="bg-white/10 rounded-lg p-3">
                      <div className="text-sm text-white/90">{aiAnalysis.sessionSummary}</div>
                    </div>
                  )}

                  <div>
                    <h4 className="text-white font-medium mb-2">AI Insights</h4>
                    <ul className="text-sm text-white/80 space-y-1">
                      {aiAnalysis.insights?.slice(0, 3).map((insight, index) => (
                        <li key={index} className="flex items-start gap-2">
                          <div className="w-1 h-1 bg-green-400 rounded-full mt-2 flex-shrink-0"></div>
                          {insight}
                        </li>
                      ))}
                    </ul>
                  </div>

                  <div>
                    <h4 className="text-white font-medium mb-2">AI Recommendations</h4>
                    <ul className="text-sm text-white/80 space-y-1">
                      {aiAnalysis.recommendations?.slice(0, 3).map((rec, index) => (
                        <li key={index} className="flex items-start gap-2">
                          <AlertCircle className="w-3 h-3 text-yellow-400 mt-0.5 flex-shrink-0" />
                          {rec}
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              </div>
            )}

            {/* Session History */}
            {sessions.length > 0 && (
              <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
                <h3 className="text-xl font-semibold text-white mb-4">AI Session History</h3>
                <div className="space-y-3">
                  {sessions.slice(-3).map((session) => (
                    <div key={session.id} className="bg-white/10 rounded-lg p-3">
                      <div className="flex justify-between items-start mb-1">
                        <div className="text-sm font-medium text-white truncate">
                          {session.goal}
                        </div>
                        <div className="text-xs text-white/60">
                          {formatTime(session.duration)}
                        </div>
                      </div>
                      <div className="flex justify-between items-center text-xs text-white/60">
                        <span>{session.timestamp}</span>
                        <div className="flex gap-2">
                          <span>{session.screenshots} AI analyses</span>
                          <span>•</span>
                          <span>{session.captureStats?.clicks || 0} clicks</span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Recent Screenshots Preview */}
        {screenshots.length > 0 && (
          <div className="mt-6">
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                <Camera className="w-5 h-5" />
                Recent AI Analyzed Screenshots
              </h3>
              <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
                {screenshots.slice(-12).map((screenshot) => (
                  <div key={screenshot.id} className="relative">
                    <img 
                      src={screenshot.url} 
                      alt="Screenshot" 
                      className="w-full h-24 object-cover rounded-lg border border-white/20"
                    />
                    <div className="absolute top-1 left-1">
                      <div className={`w-3 h-3 rounded-full ${
                        screenshot.analyzed ? 'bg-green-400' : 'bg-yellow-400 animate-pulse'
                      }`}></div>
                    </div>
                    <div className="absolute top-1 right-1">
                      <div className="bg-black/70 text-white text-xs px-1 py-0.5 rounded">
                        {screenshot.trigger}
                      </div>
                    </div>
                    <div className="absolute bottom-1 left-1 right-1">
                      <div className="bg-black/70 text-white text-xs px-1 py-0.5 rounded text-center">
                        {screenshot.timestamp}
                      </div>
                    </div>
                    {screenshot.aiAnalysis && (
                      <div className="absolute bottom-1 right-1">
                        <div className={`text-xs font-bold px-1 py-0.5 rounded ${
                          screenshot.aiAnalysis.productivityScore > 80 ? 'bg-green-500 text-white' :
                          screenshot.aiAnalysis.productivityScore > 60 ? 'bg-yellow-500 text-black' :
                          'bg-red-500 text-white'
                        }`}>
                          {screenshot.aiAnalysis.productivityScore}%
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProgressTracker;
